sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var application = g_form.getValue('application');
    if (application != null && application != undefined && application != '') {
        var rightNow = new Date();
        var year = rightNow.getFullYear();
        var month = rightNow.getMonth();
        var day = rightNow.getDate();

        var getDelais = new GlideAjax('GetDelais');
        getDelais.addParam('sysparm_name', 'getDelaiPrisEnCharge');
        getDelais.addParam('sysparm_priority', newValue);
        getDelais.addParam('sysparm_application', application);

        // Send the request to the server
        getDelais.getXML(getResponse);

        // When the response is back from the server
        function getResponse(response) {
            var delai = response.responseXML.documentElement.getAttribute("answer");
            var expectedDate = year + '-' + (month + 1) + '-' + (day + parseInt(delai));
            g_form.setValue('expected_start_new', expectedDate);

        }



        var getDelaisResolu = new GlideAjax('GetDelais');
        getDelaisResolu.addParam('sysparm_name', 'getDelaiResolution');
        getDelaisResolu.addParam('sysparm_priority', newValue);
        getDelaisResolu.addParam('sysparm_application', application);

        // Send the request to the server
        getDelaisResolu.getXML(getResponseResolu);

        // When the response is back from the server
        function getResponseResolu(response) {
            var delai = response.responseXML.documentElement.getAttribute("answer");
            var objectifResolution = year + '-' + (month + 1) + '-' + (day + parseInt(delai));
            g_form.setValue('date_objectif_de_r_solution', objectifResolution);

        }

    }

    var getGroup = new GlideAjax('GetGroup');
    getGroup.addParam('sysparm_name', 'getGroupRecord');
    if (newValue == '3') {
        getGroup.addParam('sysparm_groupName', 'Support niveau 2');
    } else {
        getGroup.addParam('sysparm_groupName', 'Support niveau 1');
    }
    // Send the request to the server
    getGroup.getXML(getResponse);

    // When the response is back from the server
    function getResponse(response) {
        var gr = response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue('group', gr);
    }


}
