sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var priority = g_form.getValue('priority');
    if (priority != null && priority != undefined && priority != '') {
        var rightNow = new Date();
        var year = rightNow.getFullYear();
        var month = rightNow.getMonth();
        var day = rightNow.getDate();

        var getDelais = new GlideAjax('GetDelais');
        getDelais.addParam('sysparm_name', 'getDelaiPrisEnCharge');
        getDelais.addParam('sysparm_priority', priority);
        getDelais.addParam('sysparm_application', newValue);

        // Send the request to the server
        getDelais.getXML(getResponse);

        // When the response is back from the server
        function getResponse(response) {
            var delai = response.responseXML.documentElement.getAttribute("answer");
            var expectedDate = year + '-' + (month + 1) + '-' + (day + parseInt(delai));; //new Date(year,month, day + delai );
            g_form.setValue('expected_start_new', expectedDate);
        }



        var getDelaisResolu = new GlideAjax('GetDelais');
        getDelaisResolu.addParam('sysparm_name', 'getDelaiResolution');
        getDelaisResolu.addParam('sysparm_priority', priority);
        getDelaisResolu.addParam('sysparm_application', newValue);

        // Send the request to the server
        getDelaisResolu.getXML(getResponseResolu);

        // When the response is back from the server
        function getResponseResolu(response) {
            var delai = response.responseXML.documentElement.getAttribute("answer");

            var inThreeDays = new Date();

            var objectifResolution = year + '-' + (month + 1) + '-' + (day + parseInt(delai)); // new Date(year,month, day + delai);
            g_form.setValue('date_objectif_de_r_solution', objectifResolution);
        }

    }


}
