sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
	// load the state
    var state = g_form.getValue('state');
	 var number = g_form.getValue('sys_created_by');

    if (state == '0') {
        g_form.clearOptions('state');
        g_form.addOption('state', '0', 'Nouveau');
		if(number != null && number != '' && number != 0)
			{
        g_form.addOption('state', '1', 'Pris en charge');
			}
    }
    if (state == '1') {
        g_form.clearOptions('state');
        g_form.addOption('state', '1', 'Pris en charge');
        g_form.addOption('state', '2', 'Résolu');
    }
    if (state == '2') {
        g_form.clearOptions('state');
        g_form.addOption('state', '2', 'Résolu');
        g_form.addOption('state', '3', 'Validé');
        g_form.addOption('state', '4', 'Rejeté');
    }
	 if (state == '3') {
        g_form.clearOptions('state');
		     g_form.addOption('state', '3', 'Validé');

    }
	 if (state == '4') {
        g_form.clearOptions('state');
		  g_form.addOption('state', '4', 'Rejeté');
    }
}
