sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var rightNow = new Date();
	var year = rightNow.getFullYear();
	var month = rightNow.getMonth();
	var day = rightNow.getDate();
	if (newValue == '0'){

		g_form.setValue('date_prise_en_charge_auto', null);
		g_form.setValue('ind_qual_prise_en_charge', 'KO');
		g_form.setValue('due_date', null);

	}
	if (newValue == '1'){

		var datePrisEnCharge = year + '-' + (month + 1) + '-' + day;
		g_form.setValue('date_prise_en_charge_auto', datePrisEnCharge);
		g_form.setValue('ind_qual_prise_en_charge', 'OK');
		g_form.setValue('due_date', null);
	}
	if (newValue == '2'){
			var dateResolution = year + '-' + (month + 1) + '-' + day ;
		g_form.setValue('date_de_resolution', dateResolution);
		g_form.setValue('ind_qual_resolution', 'KO');


		 var getGroup = new GlideAjax('GetGroup');
		 getGroup.addParam('sysparm_name','getGroupRecord');


		getGroup.addParam('sysparm_groupName', 'Support validation');
		 getGroup.getXML(getResponse);

	   // When the response is back from the server
    function getResponse(response){
        var gr = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('group', gr);
	}
	}
	if (newValue == '3'){
		//	var dateValidation = year + '-' + (month + 1) + '-' + day ;
		//g_form.setValue('date_de_resolution', dateValidation);
		g_form.setValue('ind_qual_resolution', 'OK');
	}
	if (newValue == '4'){
		g_form.setValue('date_de_resolution', null);
		g_form.setValue('date_prise_en_charge_auto', null);
		g_form.setValue('ind_qual_resolution', 'KO');
		g_form.setValue('ind_qual_resolution', 'KO');
	}
   //Type appropriate comment here, and begin script below

}
