sys_script_include.script:
var GetDelais = Class.create();
GetDelais.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDelaiPrisEnCharge: function() {
        var delais = new GlideRecord("x_509970_tuleap_configuration_delais");
        delais.addQuery('severite', '=', this.getParameter('sysparm_priority'));
        delais.addQuery('application_name', '=', this.getParameter('sysparm_application'));
        delais.setLimit(1);
        delais.query();
        if (delais.next()) {

            return delais.delai_pris_en_charge;
        }

    },
    getDelaiResolution: function() {
        var delais = new GlideRecord("x_509970_tuleap_configuration_delais");
        delais.addQuery('severite', '=', this.getParameter('sysparm_priority'));
        delais.addQuery('application_name', '=', this.getParameter('sysparm_application'));
        delais.setLimit(1);
        delais.query();
        if (delais.next()) {
            return delais.delai_resolution;
        }

    },
    type: 'GetDelais'
});
